var HelpModule = function() {
	var self = this;
	
	self.helpListener = function(from, to, text, message) {
		if (text == '!help') {
			this.notice(from, "I am owned by Pietdagamer, contact him for suggestions, feedback or problems.");
			
			this.notice(from, "My commands are: ");
			this.notice(from, "!quote: ");
			this.notice(from, "  Shows a random text someone has said in the channel");

			console.log(message)
		}
	};
	
	self.helpListenerPublic = function(from, to, text, message) {
		if (text == "!help") {
			this.notice(from, "Use /notice WhatBot !help")
		}
	}

  /**
   * Unloads all listeners
  */
  self.unload = function(client) {
    client.removeListener('notice', self.helpListener);
    client.removeListener('message', self.helpListenerPublic);
  };

  /**
   * Loads all listeners
  */
  self.load = function(client) {
    client.addListener('notice', self.helpListener);
    client.addListener('message', self.helpListenerPublic);
  };

};

module.exports = new HelpModule();